{% comment %}
  TRACK ORDER – Single-file template
  Create: templates/page.track-order.liquid
  Assign this template to a page called “Track your order”.
{% endcomment %}

<div id="trackroot" style="--brand-bg:#ffffff;--brand-card:#eef3f6;--brand-text:#0b0b0b;--brand-muted:#6b7280;--brand-primary:#1f5cff;--brand-primary-ink:#ffffff;--brand-border:#e5e7eb;--radius:14px;--shadow:0 8px 24px rgba(0,0,0,.07);--max:1200px;">
  <style>
    #trackroot{font-family:inherit;background:var(--brand-bg);padding:48px 16px;}
    .t-wrap{max-width:var(--max);margin:0 auto;}
    .t-head{display:flex;flex-direction:column;gap:12px;margin-bottom:24px;}
    .t-eyebrow{font-weight:600;color:var(--brand-muted);letter-spacing:.08em;text-transform:uppercase;}
    .t-title{font-weight:800;font-size:clamp(28px,4vw,40px);line-height:1.1;color:var(--brand-text);}
    .t-sub{color:var(--brand-muted);font-size:clamp(14px,2.2vw,16px);}
    .t-grid{display:grid;gap:20px;grid-template-columns: 1fr; }
    @media(min-width:900px){ .t-grid{grid-template-columns:2fr 1fr;} }

    .card{background:var(--brand-card);border:1px solid var(--brand-border);border-radius:var(--radius);box-shadow:var(--shadow);padding:24px;}
    .card h3{margin:0 0 10px 0;font-weight:800;color:var(--brand-text);font-size:clamp(18px,2.4vw,22px);}
    .field{display:flex;gap:12px;flex-wrap:wrap;margin:6px 0 14px;}
    .field input[type="text"], .field input[type="email"]{
      flex:1 1 240px;padding:14px 14px;border-radius:10px;border:1px solid var(--brand-border);
      background:#fff;color:#111;font-size:16px;outline:none;
    }
    .field input:focus{border-color:var(--brand-primary);box-shadow:0 0 0 3px rgba(31,92,255,.15);}
    .btn{appearance:none;border:0;border-radius:12px;background:var(--brand-primary);color:var(--brand-primary-ink);
      padding:14px 18px;font-weight:800;cursor:pointer;display:inline-flex;align-items:center;gap:10px;text-decoration:none;}
    .btn:disabled{opacity:.6;cursor:not-allowed;}
    .hint{color:var(--brand-muted);font-size:13px;}
    .hr{height:1px;background:var(--brand-border);margin:20px 0;}
    .bullets{margin:10px 0 0 16px;color:var(--brand-muted);font-size:14px;}
    .bullets li{margin:6px 0;}
    .support h4{margin:0 0 8px 0;font-weight:800}
    .support p{margin:0;color:var(--brand-muted)}
    .badge{display:inline-block;background:#fff;border:1px solid var(--brand-border);padding:6px 10px;border-radius:24px;font-size:12px;color:#111}
    .stack{display:flex;gap:10px;flex-wrap:wrap}
    .carrier-list{display:flex;flex-wrap:wrap;gap:8px;margin-top:10px}
    .carrier{background:#fff;border:1px solid var(--brand-border);border-radius:999px;padding:6px 10px;font-size:12px;color:#111}
  </style>

  <div class="t-wrap">
    <div class="t-head">
      <span class="t-eyebrow">Order Tracking</span>
      <h1 class="t-title">Track your order</h1>
      <p class="t-sub">Drop your tracking number below and we’ll route you straight to the carrier page. If you only have the Shopify “View your order” link, use the status option.</p>
    </div>

    <div class="t-grid">
      <!-- Left: Tracking by number -->
      <div class="card">
        <h3>Track by <span class="badge">Tracking Number</span></h3>
        <form id="trackForm" onsubmit="return false;">
          <div class="field">
            <input id="trackInput" type="text" placeholder="e.g. 1Z999AA10123456784 / 9400 1000 0000 0000 0000 00" autocomplete="off" inputmode="text">
            <button id="trackBtn" class="btn">Track package →</button>
          </div>
          <div class="hint">We auto-detect UPS, USPS, FedEx, DHL, Canada Post & more. You’ll be redirected to the correct carrier.</div>
          <div id="carrierChips" class="carrier-list" aria-hidden="true" style="display:none;"></div>
        </form>
        <div class="hr"></div>
        <ul class="bullets">
          <li>If your number just went live, carriers may take a little time to show movement.</li>
          <li>Multiple numbers? Track one at a time.</li>
        </ul>
      </div>

      <!-- Right: Order status help -->
      <div class="card support">
        <h3>Track via <span class="badge">Order Status</span></h3>
        <p>Open the **“View your order”** link from your confirmation email—we’ll show live status and any tracking numbers attached to your order.</p>
        <div class="stack" style="margin-top:12px;">
          <a class="btn" href="/account/login">Log in to your account</a>
          <a class="btn" href="/pages/contact">Need help?</a>
        </div>
        <div class="hr"></div>
        <h4>Questions? We’re here.</h4>
        <p><strong>Email:</strong> sales@cacoamerica.com<br>
           <strong>Phone:</strong> +1 (305) 507-1190 • +1 (800) 291-1718<br>
           <strong>Hours:</strong> Mon–Fri 8am–5pm EST • Sat 8am–12pm EST</p>
        <p class="hint" style="margin-top:8px;">Details per Caco America’s contact page.</p>
      </div>
    </div>

    <!-- Bottom info card -->
    <div class="card" style="margin-top:20px;">
      <h3>What carriers we support out-of-the-box</h3>
      <div class="carrier-list">
        <span class="carrier">UPS</span>
        <span class="carrier">USPS</span>
        <span class="carrier">FedEx</span>
        <span class="carrier">DHL Express</span>
        <span class="carrier">Canada Post</span>
        <span class="carrier">Lasership</span>
      </div>
    </div>
  </div>

  <script>
    (function(){
      // Minimal carrier detector (expand as needed)
      const rules = [
        {name:'UPS',      test:v=>/^1Z[0-9A-Z]{16}$/i.test(v.replace(/\s/g,'')),                   url:v=>`https://www.ups.com/track?tracknum=${encodeURIComponent(v)}`},
        {name:'USPS',     test:v=>/^\d{20,22}$/.test(v.replace(/\s/g,'')) || /^94\d{20}$/.test(v.replace(/\s/g,'')), url:v=>`https://tools.usps.com/go/TrackConfirmAction?qtc_tLabels1=${encodeURIComponent(v)}`},
        {name:'FedEx',    test:v=>/^\d{12,15}$/.test(v.replace(/\s/g,'')),                         url:v=>`https://www.fedex.com/fedextrack/?trknbr=${encodeURIComponent(v)}`},
        {name:'DHL',      test:v=>/^\d{10}$/.test(v.replace(/\s/g,'')) || /^JD\d{18,}/i.test(v.replace(/\s/g,'')), url:v=>`https://www.dhl.com/global-en/home/tracking/tracking-express.html?tracking-id=${encodeURIComponent(v)}`},
        {name:'Canada Post', test:v=>/^[A-Z]\d[A-Z]\s?\d[A-Z]\d$/i.test(v) || /^\d{16}$/.test(v.replace(/\s/g,'')), url:v=>`https://www.canadapost-postescanada.ca/track-reperage/en#/search?searchFor=${encodeURIComponent(v)}`},
        {name:'Lasership', test:v=>/^(LX|1LS)\d+/i.test(v.replace(/\s/g,'')),                       url:v=>`https://www.lasership.com/track/${encodeURIComponent(v)}`},
      ];

      const $ = s => document.querySelector(s);
      const chips = $('#carrierChips');

      function guess(v){
        const hits = rules.filter(r=>r.test(v));
        return hits.length ? hits : null;
      }

      function showChips(hits){
        if(!hits || !hits.length){ chips.style.display='none'; chips.setAttribute('aria-hidden','true'); chips.innerHTML=''; return; }
        chips.innerHTML = hits.map(h=>`<span class="carrier">${h.name}</span>`).join('');
        chips.style.display='flex'; chips.setAttribute('aria-hidden','false');
      }

      $('#trackInput').addEventListener('input', e=>{
        const v = e.target.value.trim();
        const hits = v ? guess(v) : null;
        showChips(hits);
      });

      $('#trackBtn').addEventListener('click', ()=>{
        const raw = $('#trackInput').value.trim();
        if(!raw){ alert('Please enter a tracking number.'); return; }
        const hits = guess(raw);
        if(hits && hits.length){ window.location.href = hits[0].url(raw); }
        else{
          // Fallback: try UPS tracking as generic (they sometimes resolve others)
          window.location.href = `https://www.17track.net/en?nums=${encodeURIComponent(raw)}`;
        }
      });
    })();
  </script>
</div>
