settings.custom_liquid:
{%- if product -%}
{%- comment -%}
Breadcrumb + Subcategory Prev/Next — POPUP 210px • thumb 50x50 sin padding vertical • gap 0 con texto • title PR=15px / PL=23px
{%- endcomment -%}

{%- assign MENU_HANDLE = 'main-menu' -%}
{%- assign url_collection = collection -%}
{%- assign prod_col_handles = product.collections | map: 'handle' -%}

{%- assign active_collection = nil -%}

{%- comment -%}Nav fallback: linklists (legacy) -> menus (OS2){%- endcomment -%}
{%- assign nav = linklists[MENU_HANDLE] -%}
{%- if nav == nil -%}
  {%- assign nav = menus[MENU_HANDLE] -%}
{%- endif -%}

{%- if nav and nav.links and nav.links.size > 0 -%}
  {%- assign deep_match = nil -%}
  {%- assign mid_match  = nil -%}
  {%- assign top_match  = nil -%}
  {%- for l1 in nav.links -%}
    {%- if l1.type == 'collection_link' and l1.object and prod_col_handles contains l1.object.handle -%}
      {%- assign top_match = l1.object -%}
    {%- endif -%}
    {%- if l1.links and l1.links.size > 0 -%}
      {%- for l2 in l1.links -%}
        {%- if l2.type == 'collection_link' and l2.object and prod_col_handles contains l2.object.handle -%}
          {%- assign mid_match = l2.object -%}
        {%- endif -%}
        {%- if l2.links and l2.links.size > 0 -%}
          {%- for l3 in l2.links -%}
            {%- if l3.type == 'collection_link' and l3.object and prod_col_handles contains l3.object.handle -%}
              {%- assign deep_match = l3.object -%}{%- break -%}
            {%- endif -%}
          {%- endfor -%}
        {%- endif -%}
        {%- if deep_match -%}{%- break -%}{%- endif -%}
      {%- endfor -%}
    {%- endif -%}
    {%- if deep_match -%}{%- break -%}{%- endif -%}
  {%- endfor -%}
  {%- assign active_collection = deep_match | default: mid_match | default: top_match -%}
{%- endif -%}

{%- if active_collection == nil -%}{%- assign active_collection = url_collection -%}{%- endif -%}
{%- if active_collection == nil -%}{%- assign active_collection = product.collections.first -%}{%- endif -%}

{%- assign first_product = nil -%}
{%- assign last_product  = nil -%}
{%- assign prev_product  = nil -%}
{%- assign next_product  = nil -%}
{%- assign prev_candidate = nil -%}
{%- assign found_current = false -%}

{%- if active_collection and active_collection.products_count > 0 -%}
  {%- for p in active_collection.products -%}
    {%- if forloop.first -%}{%- assign first_product = p -%}{%- endif -%}
    {%- assign last_product = p -%}
    {%- if p.id == product.id -%}
      {%- assign prev_product = prev_candidate -%}
      {%- assign found_current = true -%}
    {%- elsif found_current and next_product == nil -%}
      {%- assign next_product = p -%}
      {%- assign found_current = false -%}
    {%- endif -%}
    {%- assign prev_candidate = p -%}
  {%- endfor -%}
  {%- if prev_product == nil -%}{%- assign prev_product = last_product -%}{%- endif -%}
  {%- if next_product == nil -%}{%- assign next_product = first_product -%}{%- endif -%}
{%- endif -%}

<style>
  .product-breadcrumbs, .breadcrumbs-share-list, .mh-bcbar { overflow: visible !important; }
  .mh-bcbar{ display:flex; align-items:center; justify-content:space-between; gap:16px; position:relative; z-index:10; isolation:isolate; }
  .mh-left{ min-width:0; }
  .mh-nav{ display:flex; align-items:center; gap:12px; position:relative; z-index:20; }

  /* Nav gray + hover blue */
  .mh-nav .mh-iconbtn{ color:#9aa3ad; }
  .mh-nav .mh-iconbtn:hover{ color:#1a73e8; }
  .mh-nav .mh-iconbtn:focus-visible{ outline:2px solid currentColor; outline-offset:2px; }

  .mh-iconbtn{
    --size:40px; width:var(--size); height:var(--size);
    background:transparent; border:none; border-radius:0;
    display:inline-flex; align-items:center; justify-content:center;
    cursor:pointer; position:relative; box-shadow:none;
    transition: transform .08s ease, opacity .15s ease, color .15s ease;
  }
  .mh-iconbtn svg{ width:20px; height:20px; opacity:.95; }

  /* REPLACED: Card 210px — default hidden; when open moved to body & becomes interactive */
  .mh-preview{
    position:absolute;
    top:36px; right:-4px;
    width:210px; background:#fff; border-radius:12px;
    border:1px solid #e5e7eb; box-shadow:0 12px 30px rgba(0,0,0,.16);
    padding:10px 0;
    display:none; z-index:9999;
    pointer-events:none;
    transform: translateY(2px); opacity:0;
    transition: transform .16s cubic-bezier(.2,.8,.2,1), opacity .16s ease;
    overflow:hidden; will-change: transform, opacity;
  }
  .mh-preview.is-open{ display:block; transform:translateY(0); opacity:1; pointer-events:auto; }
  .mh-preview.is-floating{
    position:fixed !important;
    top: var(--mh-top, 0px);
    left: var(--mh-left, 0px);
    right:auto; bottom:auto;
    z-index:10000000;
    pointer-events:auto;
    transform:none; opacity:1; overflow:visible;
  }

  /* GRID: 50px thumb + 0 gap with text */
  .mh-preview .row{
    display:grid; grid-template-columns: 50px 1fr;
    column-gap:0; row-gap:0; align-items:center;
  }

  /* Thumb 50x50 — no vertical padding; lateral spacing ML=18 / MR=8 */
  .mh-preview .thumb{
    width:50px; height:50px; border-radius:8px;
    grid-row:1 / span 2; grid-column:1;
    display:flex; align-items:center; justify-content:center;
    margin:0 8px 0 18px; padding:0; background:#fff; overflow:hidden;
  }
  .mh-preview .thumb img{
    width:100%; height:100%; object-fit:contain; display:block; margin:0; border:0;
  }

  /* Text padding; PR=15 and PL=23 */
  .mh-preview .title{
    grid-column:2; grid-row:1;
    color:#1761A2;
    font-size:13px; font-weight:700; line-height:1.2;
    margin:0; padding:0 15px 0 23px;
    display:-webkit-box; -webkit-line-clamp:2; -webkit-box-orient:vertical; overflow:hidden; text-align:left;
  }
  .mh-preview .sku{
    grid-column:2; grid-row:2;
    color:#6b7280;
    font-size:11.5px; font-weight:600; line-height:1.1;
    margin:0; padding:0 15px 0 23px; white-space:nowrap; opacity:.9; text-align:left;
  }

  @media (max-width:767px){
    .mh-bcbar{ flex-direction:column; gap:10px; }
    .mh-nav{ display:none !important; }
    .breadcrumbs{ width:100%; }
    .breadcrumbs__list{ display:flex; flex-wrap:wrap; gap:8px; justify-content:center; text-align:center; width:100%; }
    .breadcrumbs__item{ display:inline-flex; }
  }
</style>

<div class="page-width container-fluid">
  <div class="product-breadcrumbs mb-4">
    <div class="row">
      <div class="col-12">
        <div class="mh-bcbar">
          <div class="mh-left breadcrumbs-share-list">
            <nav class="breadcrumbs" role="navigation" aria-label="breadcrumbs">
              <ol class="breadcrumbs__list">
                <li class="breadcrumbs__item">
                  <a class="breadcrumbs__link link" href="/" title="Home">Home</a>
                </li>

                <!-- 1) Products -> All Products -->
                <li class="breadcrumbs__item">
                  <a class="breadcrumbs__link link" href="/collections/all" title="All Products">All Products</a>
                </li>

                <!-- 2) Main category (top_match) BEFORE active_collection -->
{%- assign top_is_all = false -%}
{%- if top_match -%}
  {%- assign top_title_down = top_match.title | downcase -%}
  {%- if top_match.handle == 'all' or top_match.url == '/collections/all' or top_title_down == 'all products' or top_title_down == 'products' -%}
    {%- assign top_is_all = true -%}
  {%- endif -%}
{%- endif -%}

{%- if top_match and top_is_all == false and active_collection and top_match.handle != active_collection.handle -%}
  <li class="breadcrumbs__item">
    <a class="breadcrumbs__link link" href="{{ top_match.url }}" title="{{ top_match.title }}">{{- top_match.title -}}</a>
  </li>
{%- endif -%}


                {%- comment -%}
                  Optional: if deep_match exists, show mid_match in between (keeps hierarchy clean).
                  This does NOT break your requested case; it only improves deeper trees.
                {%- endcomment -%}
                {%- if deep_match and mid_match and active_collection and active_collection.handle == deep_match.handle -%}
                  {%- if top_match == nil or mid_match.handle != top_match.handle -%}
                    <li class="breadcrumbs__item">
                      <a class="breadcrumbs__link link" href="{{ mid_match.url }}" title="{{ mid_match.title }}">{{- mid_match.title -}}</a>
                    </li>
                  {%- endif -%}
                {%- endif -%}

                {% if active_collection %}
                  <li class="breadcrumbs__item">
                    <a class="breadcrumbs__link link" href="{{ active_collection.url }}" title="{{ active_collection.title }}">{{- active_collection.title -}}</a>
                  </li>
                {% endif %}

                <li class="breadcrumbs__item">
                  <span class="breadcrumbs__link_no" aria-current="page">{{ product.title }}</span>
                </li>
              </ol>
            </nav>
          </div>

          {% if active_collection and prev_product and next_product %}
            {% assign prev_img  = prev_product.featured_image | default: nil %}
            {% assign next_img  = next_product.featured_image | default: nil %}
            {% assign prev_sku  = prev_product.selected_or_first_available_variant.sku | default: '' %}
            {% assign next_sku  = next_product.selected_or_first_available_variant.sku | default: '' %}

            <div class="mh-nav" aria-label="Adjacent product navigation">
              <!-- Prev -->
              <a class="mh-iconbtn"
                 href="{{ prev_product.url | within: active_collection }}"
                 aria-label="Previous product"
                 data-preview-title="{{ prev_product.title | escape }}"
                 data-preview-sku="{{ prev_sku | escape }}"
                 data-preview-img="{% if prev_img %}{{ prev_img | image_url: width: 220 }}{% endif %}">
                <svg viewBox="0 0 24 24" aria-hidden="true" focusable="false"><path d="M15 6l-6 6 6 6" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/></svg>
                <div class="mh-preview" role="dialog" aria-label="Previous product preview" aria-hidden="true">
                  <div class="row">
                    <div class="thumb"><img alt="" src=""></div>
                    <div class="title"></div>
                    <div class="sku"></div>
                  </div>
                </div>
              </a>

              <!-- Subcategory -->
              <a class="mh-iconbtn" href="{{ active_collection.url }}" aria-label="Back to {{ active_collection.title | escape }}">
                <svg viewBox="0 0 24 24" aria-hidden="true" focusable="false">
                  <g fill="currentColor">
                    <circle cx="5" cy="5" r="2"/><circle cx="12" cy="5" r="2"/><circle cx="19" cy="5" r="2"/>
                    <circle cx="5" cy="12" r="2"/><circle cx="12" cy="12" r="2"/><circle cx="19" cy="12" r="2"/>
                    <circle cx="5" cy="19" r="2"/><circle cx="12" cy="19" r="2"/><circle cx="19" cy="19" r="2"/>
                  </g>
                </svg>
              </a>

              <!-- Next -->
              <a class="mh-iconbtn"
                 href="{{ next_product.url | within: active_collection }}"
                 aria-label="Next product"
                 data-preview-title="{{ next_product.title | escape }}"
                 data-preview-sku="{{ next_sku | escape }}"
                 data-preview-img="{% if next_img %}{{ next_img | image_url: width: 220 }}{% endif %}">
                <svg viewBox="0 0 24 24" aria-hidden="true" focusable="false"><path d="M9 6l6 6-6 6" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/></svg>
                <div class="mh-preview" role="dialog" aria-label="Next product preview" aria-hidden="true">
                  <div class="row">
                    <div class="thumb"><img alt="" src=""></div>
                    <div class="title"></div>
                    <div class="sku"></div>
                  </div>
                </div>
              </a>
            </div>
          {% endif %}
        </div>
      </div>
    </div>
  </div>
</div>

<script>
/* REPLACEMENT SCRIPT: move preview to body to avoid clipping; Esc-to-close; fix link navigation (no preventDefault on click) */
(function(){
  const clamp = (v, a, b) => Math.max(a, Math.min(v, b));

  document.querySelectorAll('.mh-iconbtn').forEach(function(btn){
    const card = btn.querySelector('.mh-preview');
    if (!card) return;

    const imgEl = card.querySelector('.thumb img');
    const titleEl = card.querySelector('.title');
    const skuEl = card.querySelector('.sku');

    // Keep references to restore DOM position later
    let originalParent = null;
    let originalNext = null;
    let isOpen = false;

    function fill() {
      const src = btn.getAttribute('data-preview-img') || '';
      const t = btn.getAttribute('data-preview-title') || '';
      const s = btn.getAttribute('data-preview-sku') || '';
      if (src) imgEl.src = src;
      titleEl.textContent = t;
      skuEl.textContent = s ? ('SKU: ' + s) : 'SKU: —';
    }

    function measureAndPlace() {
      const r = btn.getBoundingClientRect();
      const vw = window.innerWidth, vh = window.innerHeight;
      const popupW = 210, GAP = 4;

      // Temporarily show to measure size
      card.style.visibility = 'hidden';
      card.style.display = 'block';
      const measuredH = card.offsetHeight || 96;
      card.style.display = '';
      card.style.visibility = '';

      let top = r.bottom + GAP;
      let left = r.right - popupW;

      left = clamp(left, 8, vw - popupW - 8);
      if (top + measuredH > vh - 8) top = r.top - measuredH - GAP;
      top = clamp(top, 8, vh - measuredH - 8);

      card.style.setProperty('--mh-top', top + 'px');
      card.style.setProperty('--mh-left', left + 'px');
    }

    function open() {
      if (isOpen) return;
      fill();

      // move to body so it won't be clipped by PDP or other ancestors
      if (card.parentNode !== document.body) {
        originalParent = card.parentNode;
        originalNext = card.nextSibling;
        document.body.appendChild(card);
      }

      card.style.pointerEvents = 'auto'; // allow interaction
      card.style.zIndex = '10000000';
      card.classList.add('is-open', 'is-floating');
      card.setAttribute('aria-hidden', 'false');

      measureAndPlace();
      window.addEventListener('resize', measureAndPlace);
      window.addEventListener('scroll', measureAndPlace, { passive: true });
      window.addEventListener('keydown', onKeyDown);
      isOpen = true;
    }

    function close() {
      if (!isOpen) return;
      card.classList.remove('is-open', 'is-floating');
      card.setAttribute('aria-hidden', 'true');
      card.style.pointerEvents = '';
      card.style.zIndex = '';

      // restore original DOM position so theme DOM remains predictable
      if (originalParent && originalParent !== document.body) {
        if (originalNext) originalParent.insertBefore(card, originalNext);
        else originalParent.appendChild(card);
        originalParent = null;
        originalNext = null;
      }

      window.removeEventListener('resize', measureAndPlace);
      window.removeEventListener('scroll', measureAndPlace);
      window.removeEventListener('keydown', onKeyDown);
      isOpen = false;
    }

    function onKeyDown(e) {
      if (e.key === 'Escape' || e.key === 'Esc') close();
    }

    // Interactions — hover/focus
    btn.addEventListener('mouseenter', open);
    btn.addEventListener('mouseleave', close);
    btn.addEventListener('focus', open, true);
    btn.addEventListener('blur', close, true);

    // allow anchor navigation — do NOT preventDefault()
    btn.addEventListener('click', function(){});
  });
})();
</script>
{%- endif -%}
